mapper.update:
UPDATE transaction
receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, dlvy_addr = #{dlvyAddr:VARCHAR}, dlvy_request = #{dlvyRequest:VARCHAR}, dlvy_date = TO_DATE(#{dlvyDate:DATE}, 'YYYY-MM-DD')
WHERE tran_no = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT t.tran_no, t.receiver_name, t.tran_status_code, t.order_date, p.prod_name, p.prod_no FROM transaction t, product p
p.prod_no = t.prod_no(+)
AND buyer_id = #{searchKeyword}
ORDER BY order_date DESC ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT tran_no, receiver_name, receiver_phone, tran_status_code
					FROM transaction
					
						
							
								
					 				buyer_id = #{searchKeyword}
																				
							
						
					 ) countTable
